meta.script:
	var enableGoogleServices=true;
	if(typeof(ECB)==='undefined'){
		ECB = {};
	}
    ECB.language = 'en';
	ECB.noBreakRefresh = false;

div.script:
(function() {
    var afterPrint = function() {
        tracker = ga.getAll()[0];
        tracker.send({
          'hitType': 'event',
          'eventCategory': 'print',
          'eventAction': 'print',
          'eventLabel': window.location.pathname,
          'page': window.location.pathname
        });
    };
    if (window.matchMedia) {
        var mediaQueryList = window.matchMedia('print');
        mediaQueryList.addListener(function(mql) {
            if (!mql.matches) {
                afterPrint();
            }
        });
    }



    var addSocialMediaTracking = function(){
    	var sl = document.querySelectorAll('ul.socialSharing>li');
    	for(var i=0;i<sl.length;i++)
    	{
    		sle = sl[i];
    		var slClass = sle.classList[0];

    		var a = function(s){
    			sle.onclick = function(e){
    			tracker = ga.getAll()[0];
          tracker.send({
    		      		'hitType': 'event',
    	      			'eventCategory': 'share',
    	        	  'eventAction': s,
    				      'eventLabel': window.location.pathname,
            	    'page': window.location.pathname
    	  		});

    			}
    		};
    		a(slClass);
    	}
    }

    addSocialMediaTracking();


    window.onafterprint = afterPrint;
}());
